import axios from "axios";

const url = 'http://localhost:3001/inventory'; 
export const fetchSodas = () => axios.get(url).then().catch((error) => (console.log(error.message)) );
export const updateSoda = (id, updatedSoda) => axios.patch(`${url}/${id}`, updatedSoda);
