import { createAsyncThunk, createSlice } from "@reduxjs/toolkit"

const DefaultAccountBalance = 5
const sodaList = []


//react-redux-thunk
//used to handle api requests
const fetchAllSodas = createAsyncThunk(
    'test',
    async(thunkAPI) => {
        const response = await api.fetchSodas()
        return response.data
    }
)

export const VendingMachineSlice = createSlice(
    {
        name: 'Sodas',
        initialState: {
            accountBalance: DefaultAccountBalance,
            sodas: sodaList
        },
    
    reducers: {
        updateSoda: (state, action) => {
          
            console.log(action)
        },
        getSodas: (state, action) => {
            state = action.payload
            console.log(state.sodas.filter((soda) => soda.name = action.payload.soda))
            console.log(action)
        }
    }
})

export const { updateSoda, getSodas } = VendingMachineSlice.actions
export default VendingMachineSlice.reducer


//NOTES
// each soda in sodalist should be accessible for manipulation for entire app
// name, desc, price, quantity, maxQuantity, available
