import React, { useEffect } from 'react';
// import * as api from '../../../api/api'

import { saveAs } from 'file-saver';

import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import TextField from '@mui/material/TextField';

export default function ProductDetails() {

const [open, setOpen] = React.useState(false);
//   const [succesfulPurchase, setSuccessfulPurcahse] = React.useState();
const [sodas, setSoda] = React.useState([]);//sodaList
const [price, setPrice] = React.useState(0);//soda quantity

//calling Server to set soda state
// useEffect(async () => {
//   const response = api.fetchSodas().then((res) => setInitialSodaStates(res.data));
// }, [succesfulPurchase]);

//calling server to update sodas
// useEffect(async () => {
//   const response = api.updateSoda(selectedSoda._id, selectedSoda.quantity)
// },
// [succesfulPurchase])//after a successful purchase is made
  

//update soda price
const handleQtyInput = (event) => {
  setPrice(Number(event.target.value) || '');
}

const handleRestock = (event) => {
    //restock api
}

//opens control panel
const handleClickOpen = () => {
  setOpen(true);
};

//closes control panel
const handleClose = (event, reason) => {
  if (reason !== 'backdropClick') {
    setOpen(false);
  }
};

console.log(sodas);
  return (
    <div >
      <Button className='product-details' variant="outlined" onClick={handleClickOpen}>Details</Button>
      <Dialog disableEscapeKeyDown open={open} onClose={handleClose}>
        <DialogTitle>{props.sodaName}</DialogTitle>
        <DialogContent>
          <Box component="form" sx={{ display: 'flex', flexWrap: 'wrap' }}>
            <FormControl sx={{ m: 1, minWidth: 120 }}>
              <TextField inputProps={{ inputMode: 'numeric', pattern: '[0-9]*' }} id="outlined-basic" label="Price" variant="outlined" value={price} onChange={(event)=>handleQtyInput(event)}/>
            </FormControl>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={() =>{
            handleClose();
          }}>
            Confirm
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
