// import { createAction } from "@reduxjs/toolkit";

import axios from "axios";

const url = 'http://localhost:3001/api'; 

export const getSodas = () => axios.get(`${url}/sodas`)
.then(res => res.data)
// .then().catch((error) => (console.log(error.message)));

export const updateSodaStock = (id, purchase) => axios.post(`${url}/stock${id}`, purchase)
.then().catch((error) => (console.log(error.message)));

export const restockSoda =(id, amount) => axios.post(`${url}/restock${id}`, amount)
.then().catch((error) => (console.log(error.message)));

export const updateSodaPrice =(id, price) => axios.post(`${url}/price${id}`, price)
.then().catch((error) => (console.log(error.message)));
