import React from 'react';


import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import InputLabel from '@mui/material/InputLabel';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import TextField from '@mui/material/TextField';



//set the sodas with a get request from the server

export default function DialogSelect() {


  const [open, setOpen] = React.useState(false);
  const [soda, setSoda] = React.useState();//soda selected - Cola as the default
  const [acctBalance, setAccBalance] = React.useState(5);
  const [qty, setQty] = React.useState(0);//soda quantity

  React.useEffect(() => {console.log('updating account balance')},[])//dependeny should account balance state

  const handleSodaInput = (event) => {
    setSoda((event.target.value));
  };

  const handleQtyInput = (event) => {
    setQty(Number(event.target.value) || '');
  }

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = (event, reason) => {
    if (reason !== 'backdropClick') {
      setOpen(false);
    }
  };


  return (
    <div>
      <Button onClick={handleClickOpen}>Control Panel</Button>
      <Dialog disableEscapeKeyDown open={open} onClose={handleClose}>
        <DialogTitle>Soda Selection</DialogTitle>
        <DialogContent>
          <Box component="form" sx={{ display: 'flex', flexWrap: 'wrap' }}>
            <FormControl sx={{ m: 1, minWidth: 120 }}>
              <InputLabel htmlFor="demo-dialog-native">Soda</InputLabel>
              <Select
                native
                defaultValue={null}
                onChange={handleSodaInput}
              >
                {/* {sodas.map(soda => (
                  <option key={soda.id} value={soda.name}>{soda.name}</option>
                ))} */}
              </Select>
            </FormControl>
            <FormControl sx={{ m: 1, minWidth: 120 }}>
              <TextField inputProps={{ inputMode: 'numeric', pattern: '[0-9]*' }} id="outlined-basic" label="Qty" variant="outlined" value={qty} onChange={(event)=>handleQtyInput(event)}/>
            </FormControl>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={() =>{
            handleClose();
            // dispatch(updateSodaQuantity({soda, qty}));
          }}>
            Purchase
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
