import React, { useEffect } from 'react';
// import * as api from '../../../api/api'

import { saveAs } from 'file-saver';

import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import TextField from '@mui/material/TextField';

export default function ProductDetails(props) {

const [open, setOpen] = React.useState(false);
//   const [succesfulPurchase, setSuccessfulPurcahse] = React.useState();
const [sodas, setSoda] = React.useState([]);//sodaList
const [price, setPrice] = React.useState(0);//soda quantity
const [editPriceDisplay, setEditPriceDisplay] = React.useState('none')

//calling Server to set soda state
// useEffect(async () => {
//   const response = api.fetchSodas().then((res) => setInitialSodaStates(res.data));
// }, [succesfulPurchase]);

//calling server to update sodas
// useEffect(async () => {
//   const response = api.updateSoda(selectedSoda._id, selectedSoda.quantity)
// },
// [succesfulPurchase])//after a successful purchase is made
  

//update soda price
const handleQtyInput = (event) => {
  setPrice(Number(event.target.value) || '');
}

const handleRestock = () => {
    //restock api
}

const editPrice = () => {
   (editPriceDisplay == 'none') ? setEditPriceDisplay('flex') :  setEditPriceDisplay('none')    
}

//opens control panel
const handleClickOpen = () => {
  setOpen(true);
};

//closes control panel
const handleClose = (event, reason) => {
  if (reason !== 'backdropClick') {
    setOpen(false);
    setEditPriceDisplay('none')
  }
};

console.log(sodas);
  return (
    <div >
      <Button className='product-details' variant="outlined" onClick={handleClickOpen}>Details</Button>
      <Dialog disableEscapeKeyDown open={open} onClose={handleClose}>
        <DialogTitle>{props.sodaName}</DialogTitle>
        <DialogContent>
            <div className="restock-box">
            <Button>Restock</Button>
            {/* Onclick show animation */}
            <div class="checkmark-wrapper">
                <svg class="checkmark" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 52 52">
                    <circle class="checkmark__circle" cx="26" cy="26" r="25" fill="none" />
                    <path class="checkmark__check" fill="none" d="M14.1 27.2l7.1 7.2 16.7-16.8" />
                </svg>
            </div>
            </div>
            
            <div className="price-box">
                <Button onClick={() => editPrice()}>Edit Price</Button> 
                <Box component="form" sx={{ display: editPriceDisplay, flexWrap: 'wrap' }}>
                    <FormControl sx={{ m: 1, minWidth: 120 }}>
                    <TextField inputProps={{ inputMode: 'numeric', pattern: '[0-9]*' }} id="outlined-basic" label="New Price" variant="outlined" value={price} onChange={(event)=>handleQtyInput(event)}/>
                    </FormControl>
                </Box>
            </div>
            
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={() =>{
            handleClose();
          }}>
            Confirm
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
