// import { createAction } from "@reduxjs/toolkit";

// export const fetchSodas = createAction("api/fetchSodas");
// export const updatedSoda = createAction("api/updateSoda");

import axios from "axios";

const url = 'http://localhost:3001/inventory'; 
export const fetchSodas = () => axios.get(url);
export const updateSoda = (id, updatedSoda) => axios.patch(`${url}/${id}`, updatedSoda);
