import React, { useEffect } from 'react';

import {useSelector, useDispatch} from 'react-redux';
import { purchaseSoda, fetchSodaData } from '../../../redux/sodaOperations/sodaSlice';

import * as api from '../../../sodaApi/api'

import { saveAs } from 'file-saver';

import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import TextField from '@mui/material/TextField';

//given an object convert to JSON
function fileDownLoad (soda, purchaseQty) {
  let purchase = {
    name: soda.name,
    price: soda.price,
    desc: soda.desc,
    qty: purchaseQty
  }
  let sodaJSON = new Blob(
    [JSON.stringify(purchase, null,4)],
    {
      type: "application/json"
    }
  )
  saveAs(sodaJSON, "soda.json")
}

export default function ControlPanel() {
  const dispatch = useDispatch()
  const sodaData = useSelector(state => state.sodaSlice)
  console.log(sodaData)
  
  useEffect(() => {
    dispatch(fetchSodaData())
  }, [])

  const [open, setOpen] = React.useState(false);
  const [succesfulPurchase, setSuccessfulPurcahse] = React.useState(false);
  const [sodas, setSoda] = React.useState(sodaData);//sodaList
  const [selectedSoda, setSelectedSoda] = React.useState();//cola is the default
  const [qty, setQty] = React.useState(0);//soda quantity

  

function setInitialSodaStates(data){
  setSoda(data);
  setSelectedSoda(data[0])
}
  //keeps track of soda selected
  const handleSodaInput = (event) => {
    setSelectedSoda((event.target.value));
  };

  //keeps track of soda quantity input
  const handleQtyInput = (event) => {
    setQty(Number(event.target.value) || '');
  }


function purchase(soda, sodaQty){
  //qty check
  console.log(sodaQty)
  console.log(selectedSoda)
  console.log(selectedSoda.quantity)
  if(sodaQty>selectedSoda.quantity){
    alert(`Only ${soda.quantity} ${soda.name} available`);
  }else{
    fileDownLoad(soda, sodaQty);
    //update soda Quantity
    selectedSoda.quantity = selectedSoda.quantity - sodaQty;
    console.log(selectedSoda.quantity)
    console.log(selectedSoda)
    setSuccessfulPurcahse(true);
    dispatch(purchaseSoda(soda.id, sodaQty))
  }
}
  //opens control panel
  const handleClickOpen = () => {
    setOpen(true);
  };

  //closes control panel
  const handleClose = (event, reason) => {
    if (reason !== 'backdropClick') {
      setOpen(false);
    }
  };

  return (
    <div className='controlPanel'>
      <Button onClick={handleClickOpen}>Control Panel</Button>
      <Dialog disableEscapeKeyDown open={open} onClose={handleClose}>
        <DialogTitle>Soda Selection</DialogTitle>
        <DialogContent>
          <Box component="form" sx={{ display: 'flex', flexWrap: 'wrap' }}>
            <FormControl sx={{ m: 1, minWidth: 120 }}>
              <Select
                native
                onChange={handleSodaInput}
              >
                {sodas.map(x => (
                  <option key={x._id} value={x.value}>{x.name}</option>
                ))}
              </Select>
            </FormControl>
            <FormControl sx={{ m: 1, minWidth: 120 }}>
              <TextField inputProps={{ inputMode: 'numeric', pattern: '[0-9]*' }} id="outlined-basic" label="Qty" variant="outlined" value={qty} onChange={(event)=>handleQtyInput(event)}/>
            </FormControl>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={() =>{
            handleClose();
            purchase(selectedSoda,qty);
          }}>
            Purchase
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
