import React, { useEffect } from "react";
import InventoryCard from "../invertoryCard/InventoryCard";
import * as api from '../../api/api'

//soda information will be fetched from this compnent and passed down to children
//will need to re-render component after data update
export default function InventoryCardList(props) {
    //sodas will come from MongoDB
const [sodas, setSoda] = React.useState([]);//soda selected - Cola as the default

//calling Server to set soda state
useEffect(async () => {
  const response = api.fetchSodas().then((res) => setSoda(res.data));
}, []);

    return(
        <div className='card-list'>
          {sodas.map(soda => (
            <InventoryCard key={soda._id} soda={soda} />
          ))}
        </div>
    );
}
